import React from "react";

export default class Counter extends React.Component {
  state = { count: 0 };

  constructor(props) {
    super(props);
    setInterval(() => {
      this.setState((state) => {
        return { count: state.state + 1 };
      });
    }, 1000);
  }

  render() {
    return (
      <div>
        <h1>Count : {this.state.count}</h1>
      </div>
    );
  }
}
